package com.ljppff.duitang;import android.app.Activity;import android.content.Intent;import android.os.Bundle;import android.view.View;import android.view.Window;import android.view.View.OnClickListener;import android.widget.ImageView;import android.widget.RelativeLayout;public class MoreClubActiivity extends Activity{	private RelativeLayout mRLintent1;	@Override	protected void onCreate(Bundle savedInstanceState) {		super.onCreate(savedInstanceState);		requestWindowFeature(Window.FEATURE_NO_TITLE);		setContentView(R.layout.clubdetail);		initView();			}	private void initView() {		mRLintent1 =(RelativeLayout)this.findViewById(R.id.mRLintent1);		mRLintent1.setOnClickListener(listener);			}	OnClickListener listener =new OnClickListener() {				@Override		public void onClick(View v) {			switch (v.getId()) {			case R.id.mRLintent1:				Intent intent =new Intent(getApplicationContext(), ClubDetailActiivity.class);				startActivity(intent);				break;			default:				break;			}		}	};}