package com.ljppff.fragment;import com.ljppff.duitang.ClubDetailActiivity;import com.ljppff.duitang.R;import android.content.Intent;import android.os.Bundle;import android.support.annotation.Nullable;import android.support.v4.app.Fragment;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.view.View.OnClickListener;import android.widget.RelativeLayout;public class Fragment3 extends Fragment {   	private RelativeLayout mRLintent1;	@Override	@Nullable	public View onCreateView(LayoutInflater inflater,			@Nullable ViewGroup container, @Nullable Bundle savedInstanceState) {		View view =inflater.inflate(R.layout.moreclub, container, false);		mRLintent1 =(RelativeLayout)view.findViewById(R.id.mRLintent1);		mRLintent1.setOnClickListener(listener);				return view;	}		OnClickListener listener =new OnClickListener() {				@Override		public void onClick(View v) {			switch (v.getId()) {			case R.id.mRLintent1:				Intent intent =new Intent(getActivity(), ClubDetailActiivity.class);				startActivity(intent);				break;			default:				break;			}		}	};	}